import React, {useState} from 'react';
import Axios from 'axios';

const LoginRegister = () => {
    const [usernameLoginInput, setUsernameLoginInput] = useState('');
    const [passwordLoginInput, setPasswordLoginInput] = useState('');
    const [usernameRegisterInput, setUsernameRegisterInput] = useState('');
    const [passwordRegisterInput, setPasswordRegisterInput] = useState('');
    const loginHandler = (e) => {
        e.preventDefault();
        Axios.post('http://localhost:3001/login', {
            "username": usernameLoginInput,
            "password": passwordLoginInput
        }).then((res) => {console.log(res)});
    }
    const registerHandler = (e) => {
        e.preventDefault();
        Axios.post('http://localhost:3001/register', {
            "username": usernameRegisterInput,
            "password": passwordRegisterInput
        }).then((res) => {console.log(res)});
    }
    return (
        <section className='formContainer'>
            <form onSubmit={loginHandler}>
                <h2>Login</h2>
                <label htmlFor="loginUsername">Username:</label>
                <input type="text" id='loginUsername' name='loginUsername' value={usernameLoginInput} onChange={(e) => {setUsernameLoginInput(e.target.value)}}/>
                <label htmlFor="loginPassword">Password:</label>
                <input type="loginPassword" name="loginPassword" id="loginPassword" value={passwordLoginInput} onChange={(e) => {setPasswordLoginInput(e.target.value)}}/>
                <input type="submit" value="Login" />
            </form>
            <hr />
            <form>
                <h2>Register</h2>
                <label htmlFor="registerUsername">Username:</label>
                <input type="text" id='registerUsername' name='registerUsername' value={usernameRegisterInput} onChange={(e) => {setUsernameRegisterInput(e.target.value)}}/>
                <label htmlFor="registerPassword">Password:</label>
                <input type="password" name="registerPassword" id="registerPassword" value={passwordRegisterInput} onChange={(e) => {setPasswordRegisterInput(e.target.value)}}/>
                <input type="submit" value="Register" />
            </form>
        </section>
    );
}

export default LoginRegister;
